mapper.select:
SELECT COUNT(*) FROM INFO_BOARD I JOIN USERINFO U ON I.UNO = U.UNO WHERE
I.info_Loc=#{infoLoc} AND I.info_blindstate=1
AND I.info_keyword = #{infoKeyword}

mapper.update:
update info_board set
info_title=#{infoTitle},
info_content = #{infoContent}, info_update=sysdate,
info_update_uno=#{uno},
info_keyword=#{infoKeyword}
where info_bno=#{infoBno} and uno=#{uno}

update info_board set info_blindstate = 3,
info_update=sysdate, info_update_uno = #{uno}
where
info_bno=#{infoBno}
and uno=#{uno}

mapper.select:
select max(I.INFO_BNO) from INFO_BOARD I join USERINFO U on I.UNO = U.UNO where
I.info_Loc=#{infoLoc} and I.info_bno < #{infoBno} and I.info_blindstate = 1
and I.info_keyword = #{infoKeyword}

select min(I.INFO_BNO) from INFO_BOARD I join USERINFO U on I.UNO = U.UNO where
I.info_Loc=#{infoLoc} and I.info_bno > #{infoBno} and I.info_blindstate = 1
and I.info_keyword = #{infoKeyword}

SELECT * FROM ( SELECT ROWNUM NUM, INFO.* FROM ( SELECT I.INFO_BNO, I.UNO, I.INFO_KEYWORD, I.INFO_TITLE, SUBSTR(I.INFO_CONTENT,1,40) AS INFO_CONTENT, I.INFO_LOC, I.INFO_VIEW_CNT, I.INFO_REPORT_CNT, I.INFO_BLINDSTATE, I.INFO_REGDATE, I.INFO_UPDATE, I.INFO_UPDATE_UNO, U.NICKNAME FROM INFO_BOARD I JOIN USERINFO U on I.UNO = U.UNO WHERE
I.INFO_LOC = #{infoLoc} AND I.INFO_BLINDSTATE = 1
and I.info_keyword = #{infoKeyword}
ORDER BY I.INFO_BNO DESC ) INFO ) WHERE NUM BETWEEN #{startRow} AND #{endRow}

mapper.update:
UPDATE info_board
SET info_view_cnt = info_view_cnt +	1
WHERE info_bno = #{infoBno}

mapper.select:
select info_file_no, info_bno, info_file_origin,
info_file_upload from info_file where info_bno = #{infoBno}
order by info_file_no
